    this.category,
    this.photos,
    this.publishDate,
  });

  NewModel.fromJson(dynamic json) {
    id = json['id'];
    title = json['title'];
    description = json['description'];
    category = json['category'];
    if (json['photos'] != null) {
      photos = [];
      json['photos'].forEach((v) {
        photos?.add(Photos.fromJson(v));
      });
    }
    publishDate = json['publish_date'];
  }

  int? id;
  String? title;
  String? description;
  String? category;
  List<Photos>? photos;
